globalConf:
{
  // Removed all existing comments along the file
  enabledProviders: "TEST_WEB_SDK_COPY",

  lang: "en",
  customEventMap: {
    eventMap: [
      {
        events: "*",
        args: [
          function (e) {
            return e;
          },
        ],
        method: function (e) {
          if (e.fullEventName === "login") {
          } else if (e.fullEventName === "logout") {
          }

          if (e.fullEventName === "accounts.login") {
            gigya.accounts.setAccountInfo({
              data: {
                previousLogins: (e.data.previousLogins || 0) + 1,
              },
            });
            if (typeof e.data.previousLogins !== "undefined") {
              console.log("previousLogins", e.data.previousLogins);
              if (e.data.previousLogins == 4) {
                gigya.accounts.showScreenSet({
                  screenSet: "test2-ProfileUpdate",
                  startScreen: "custom-update-favorite-car-manufacturer-screen",
                });

                alert("This is your 5th login");
              }
            }
          }
        },
      },
      {
        events: "afterResponse",
        args: [
          function (e) {
            return e;
          },
        ],
        method: function (e) {
		  if(typeof window.afterResponseLitePreferenceCenter != 'undefined') {
            window.afterResponseLite_PreferenceCenter(e);
          }
        },
      },
    ],
  },

  ajaxCallService: function(url, payload, callback)
    {
        var xmlHttp = new XMLHttpRequest();
        xmlHttp.onreadystatechange = function()
        {
            if (xmlHttp.readyState == 4 && xmlHttp.status == 200 && callback)
            {           
                callback(xmlHttp.responseText);
            }
        };
        xmlHttp.open("POST", url, false);
        xmlHttp.setRequestHeader("Content-Type", "application/json");
        xmlHttp.send(payload);
    },


  addMetadata: function (parentContainer, data) {
    var divHidden = document.createElement("div");
    divHidden.setAttribute("class", "gigya-composite-control-metadata");

    var inputHidden = document.createElement("input");
    inputHidden.setAttribute("type", "hidden");
    inputHidden.setAttribute("class", "gigya-metadata");
    inputHidden.setAttribute("name", "data." + data.id + ".text");
    inputHidden.setAttribute("value", data.description);
    inputHidden.setAttribute("data-screenset-element-id-publish", "false");
    inputHidden.setAttribute("data-screenset-roles", "template");
    inputHidden.setAttribute("data-gigya-name", "data." + data.id + ".text");
    inputHidden.setAttribute("data-original-value", data.description);

    divHidden.appendChild(inputHidden);

    parentContainer.parentNode.insertBefore(divHidden, parentContainer);
  },

  addElement: function (parentContainer, data) {
    var div = document.createElement("div");
    div.setAttribute(
      "class",
      "gigya-composite-control gigya-composite-control-textbox"
    );
    div.setAttribute("data-screenset-element-id-publish", "true");
    div.setAttribute("data-screenset-roles", "instance");

    var label = document.createElement("label");
    label.setAttribute("class", "gigya-label");
    label.setAttribute("for", "gigya-textbox-qa_" + data.id);

    div.appendChild(label);

    var span = document.createElement("span");
    span.setAttribute("class", "gigya-label-text");
    span.setAttribute("data-screenset-element-id-publish", "false");
    span.setAttribute("data-screenset-roles", "instance");
    span.innerText = data.description;

    label.appendChild(span);

    var label2 = document.createElement("label");
    label2.setAttribute(
      "class",
      "gigya-required-display gigya-reset gigya-hidden"
    );
    label2.setAttribute("data-bound-to", "data." + data.id);
    label2.setAttribute("for", "gigya-textbox-qa_" + data.id);
    label2.setAttribute("data-screenset-element-id-publish", "false");
    label2.setAttribute("data-screenset-roles", "instance");
    label2.setAttribute("aria-hidden", "true");
    label2.innerText = "*";

    label.appendChild(label2);

    var inputText = document.createElement("input");
    inputText.setAttribute("type", "text");
    inputText.setAttribute("class", "gigya-input-text");
    inputText.setAttribute("show-valid-checkmark", "true");
    inputText.setAttribute("data-gigya-type", "text");

    inputText.setAttribute("name", "data." + data.id);
    inputText.setAttribute(
      "data-screenset-element-id",
      "gigya-textbox-qa_" + data.id
    );
    inputText.setAttribute("data-screenset-element-id-publish", "true");
    inputText.setAttribute("data-screenset-roles", "instance");
    inputText.setAttribute(
      "data-gigya-name",
      "data." + data.id
    );
    inputText.setAttribute("id", "gigya-textbox-qa_" + data.id);
    inputText.setAttribute("aria-required", "false");
    inputText.setAttribute("aria-invalid", "false");

    div.appendChild(inputText);

    var span2 = document.createElement("span");
    span2.setAttribute("class", "gigya-error-msg");
    span2.setAttribute("data-bound-to", "data." + data.id);
    span2.setAttribute("data-screenset-element-id-publish", "false");
    span2.setAttribute("data-screenset-roles", "instance");
    span2.setAttribute("role", "alert");

    div.appendChild(span2);

    parentContainer.parentNode.insertBefore(div, parentContainer);

    gigya.thisScript.globalConf.addMetadata(parentContainer, data);
  },
}
